import React from "react";
import * as BooksAPI from "./BooksAPI";

const Book = (props) => {
  const handleShelfChange = (event) => {
    if (event.target.value !== "move") {
        const updateBook = async () => {
            await BooksAPI.update(props.book, event.target.value);
            const response = await BooksAPI.getAll();
            props.setBooks(response)

        }
    //   BooksAPI.update(props.book, event.target.value).then((response) =>
    //     BooksAPI.getAll().then((newBooks) => {
    //       props.setBooks(newBooks);
    //     })
    //   );
    }
  };

  const handleShelfChangeInSearchBooks = (event) => {
    if (event.target.value !== "move") {
      BooksAPI.update(props.book, event.target.value);
    }
  };

  return (
    <div className="book">
      <div className="book-top">
        <div
          className="book-cover"
          style={{
            width: 128,
            height: 193,
            backgroundImage: `url("${props.imageUrl}")`,
          }}
        ></div>
        <div className="book-shelf-changer">
          <select
            onChange={(event) => {
              if (!props.isSearchingPage) {
                handleShelfChange(event);
              } else {
                handleShelfChangeInSearchBooks(event);
              }
            }}
            defaultValue={props.bookshelf}
          >
            <option value="move" disabled>
              Move to...
            </option>
            <option value="currentlyReading">Currently Reading</option>
            <option value="wantToRead">Want to Read.</option>
            <option value="read">Read.</option>
            <option value="none">None</option>
          </select>
        </div>
      </div>
      <div className="book-title">{props.title}</div>
      <div className="book-authors">
        {props.authors && props.authors.map((author) => `${author},`)}
      </div>
    </div>
  );
};

export default Book;
